  ,Montag     VARCHAR(22)
  ,Dienstag   VARCHAR(24)
  ,Mittwoch   VARCHAR(30)
  ,Donnerstag VARCHAR(30)
  ,Freitag    VARCHAR(30)
);


/*LS_Ready4Stage_Belelgung_Raum_2*/
